$(document).ready((function(){removeSkill=e=>{let l=e.target.parentNode,t=l.getAttribute("data-id");$.ajax({type:"POST",url:"/delete_skill",data:`id=${t}`,succes:l.remove()})},showModal=e=>{let l=$("#modal");l.show();let t=e.target.childNodes[0].nodeValue;console.log(t);let i=e.target.parentNode;$("#skill").val(i.getAttribute("data-id")),$("#skillEdit").val(t),$(".close").click((function(){l.hide()}))},editSkill=()=>{let e=$("#modal"),l=$("#skill").val(),t=$("#skillEdit").val();$.ajax({type:"POST",url:"/edit_skill",data:`id=${l}&name=${t}`,success:function(){$(`[data-id="${l}"]`)[0].innerHTML=t+'<i class="fas fa-times icon-remove" onclick="removeSkill(event)"></i>',e.hide()}})}}));